import React, { Component } from "react";
import Stories from "../Stories";
import API from "../../utils/API";
import "./style.css";

// The ...props means, spread all of the passed props onto this element
// That way we don't have to define them all individually
class Homepage extends Component {
  state = {
    title: "",
    author: "",
    date: "",
    imageUrl: "",
    content: "",
    articles: [],
    delete: ""
  };

  componentDidMount() {
    this.loadArticles();
  }

  loadArticles = () => {
    API.getArticles()
      .then(res => {
        console.log(res.data);
        this.setState({ articles: res.data })
      })

      .catch(err => console.log(err));
  };

  render() {
    return (
      <div className="homePage">
        <div className="container">
          <div className="row firstRow">
          {this.state.articles.map(art => 
            <div className="col-md-6">
              <div className="imageContainer" onClick={()=> <Stories />}data-author={art.author} data-date={art.date} data-title={art.title} data-content={art.content}>
                <img className="image1" height="300px" src={art.imageUrl} />
                <div className="postTitle">{art.author}</div>
              </div>
            </div>
          )}


          </div>

        </div>

      </div>
    );
  }
}

export default Homepage;
