import React, { Component } from "react";
// import logo from "./logo.svg";
import "./style.css";
// import axios from 'axios';
import API from "../../utils/API";
import DeleteBtn from "../DeleteBtn";
import EditBtn from "../EditBtn";

class Admin extends Component {
  state = {
    title: "",
    author: "",
    date: "",
    imageUrl: "",
    imageUrl2: "",
    imageUrl3: "",
    imageUrl4: "",
    imageUrl5: "",
    content: "",
    contentFr: "",
    articles: [],
    delete: "",
    id: "",
    template: "1",
    showUpdateBtn: false,
    showTemplate2Fields: false
  };

  componentDidMount() {
    this.loadArticles();
    var date = new Date().getDate(); //Current Date
    var month = new Date().getMonth() + 1; //Current Month
    var year = new Date().getFullYear(); //Current Year
    this.setState({ date: month + '/' + date + '/' + year })
    console.log(date);

  }

  loadArticles = () => {
    API.getArticles()
      .then(res => {
        console.log(res.data);
        this.setState({ articles: res.data })
      })

      .catch(err => console.log(err));
  };


  handleInputChange = event => {
    event.preventDefault();
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
    console.log(this.state);
  };
  submitForm = () => {

    API.saveArticles({
      title: this.state.title,
      author: this.state.author,
      date: this.state.date,
      imageUrl: this.state.imageUrl,
      content: this.state.content
    })
      .then(this.setState({
        title: "",
        author: "",
        date: "",
        imageUrl: "",
        content: ""
      }))
      .then(this.loadArticles());
  }

  updateForm = () => {

    API.updateArticles({
      _id: this.state.id,
      title: this.state.title,
      author: this.state.author,
      date: this.state.date,
      imageUrl: this.state.imageUrl,
      content: this.state.content
    })
      .then(this.setState({
        title: "",
        author: "",
        date: "",
        imageUrl: "",
        content: "",
        showUpdateBtn: false
      }))
      .then(this.loadArticles());
  }

  deleteButton = function (id) {
    API.deleteArticles(id)
      .then(this.loadArticles());
  }
  editButton = function (id) {
    API.getArticlesById(id)
      .then((res) => this.setState({ title: res.data.title, author: res.data.author, date: res.data.date, imageUrl: res.data.imageUrl, content: res.data.content, id: res.data._id, showUpdateBtn: true }))
      .then(this.loadArticles());
  }

  render() {
    return (
      <div className="container mainPage">
        <div className="row">
          <div className="col-md-6">
            <form>
              <div class="input-group mb-3">
                <div class="input-group-prepend">
                  <label class="input-group-text" for="inputGroupSelect01">Template</label>
                </div>
                <select class="custom-select" id="inputGroupSelect01" name="template" value={this.state.template} onChange={this.handleInputChange}>
                  <option value="1">1</option>
                  <option value="2">2</option>
                </select>
              </div>

              <div className="form-group">
                <input type="text" className="form-control" placeholder="Title*" name="title" value={this.state.title} onChange={this.handleInputChange} />
              </div>
              <div className="form-group">
                <input type="text" className="form-control" placeholder="Author*" name="author" value={this.state.author} onChange={this.handleInputChange} />
              </div>
              <div className="form-group">
                <input type="text" className="form-control" placeholder="Date*" name="date" value={this.state.date} onChange={this.handleInputChange} />
              </div>
              <div className="form-group">
                <input type="url" className="form-control" placeholder="Image URL*" name="imageUrl" value={this.state.imageUrl} onChange={this.handleInputChange} />
              </div>


              <div className="form-group">
                {this.state.showTemplate2Fields ? <input type="url" className="form-control" placeholder="Image URL 2" name="imageUrl2" value={this.state.imageUrl2} onChange={this.handleInputChange} /> : null}
              </div>
              <div className="form-group">
                <input type="url" className="form-control" placeholder="Image URL 3" name="imageUrl3" value={this.state.imageUrl3} onChange={this.handleInputChange} />
              </div>
              <div className="form-group">
                <input type="url" className="form-control" placeholder="Image URL 4" name="imageUrl4" value={this.state.imageUrl4} onChange={this.handleInputChange} />
              </div>
              <div className="form-group">
                <input type="url" className="form-control" placeholder="Image URL 5" name="imageUrl5" value={this.state.imageUrl5} onChange={this.handleInputChange} />
              </div>

              <div className="form-group">
                <textarea className="form-control" placeholder="Content [en]*" rows="8" name="content" value={this.state.content} onChange={this.handleInputChange}></textarea>
              </div>
              <div className="form-group">
                <textarea className="form-control" placeholder="Content [fr]*" rows="8" name="contentFr" value={this.state.contentFr} onChange={this.handleInputChange}></textarea>
              </div>
            </form>
            <button type="button" id="submitBtn" className="btn btn-warning" onClick={() => { this.submitForm() }}>Submit</button>
            {this.state.showUpdateBtn ? <button type="button" id="updateBtn" className="btn btn-success" onClick={() => { this.updateForm() }}>Update</button> : null}
          </div>
          <div className="col-md-6">
            {this.state.articles.map(art => (
              <div className="articleBlock" data-block={this.state.author} >
                {art.title} by: {art.author}

                <DeleteBtn data-id={art._id} onClick={() => this.deleteButton(art._id)} />
                <EditBtn data-id={art._id} onClick={() => this.editButton(art._id)} />
              </div>

            ))}

          </div>
        </div>

      </div>
    );
  }
}

export default Admin;
